import React, { useState } from 'react';

function Post({post}) {
  const [response, setResponse] = React.useState('')
  const [resposeList, setResponseList] = React.useState([])

  const onResponsiveChange = (e) => {
    setResponse(e)
  }

  return (
    <div className="channel">
      <div>
        <div>
          <p>{post.subject}</p>
        </div>
        <div>
            <p>Sam</p>
            <p>{post.body}</p> 
        </div>
        {resposeList.length ? resposeList.map(val => {
          return <div>
            <p>Mike</p>
            <p>{val}</p> 
        </div>
        }): null}
        <div>
            <input placeholder='Response' onChange={e => onResponsiveChange(e.target.value)}/>
            <button onClick={() => setResponseList([...resposeList, response])}> Submit </button>
        </div>
        <div>
          <p> </p>
        </div>
      </div>
    </div>
  );
}

export default Post;
