import React,{ useEffect } from 'react';
import Channel from './Channel';
import Post from './Post';
import {logOut} from './services/firebase'
import { UserContext } from './providers/UserProvider';
import { useHistory } from 'react-router';
import '../src/style/main.css';
import { db } from './services/firebase';

function Dashboard() {
 // const dbRef = ref(getDatabase());
  const [channel, setChannel] = React.useState([]);
  const [selectedChannel, setSelectedChannel] = React.useState('');
  const [newPost, setNewPost] = React.useState({subject: '', body: ''});
  const [isPost, setIsPost] = React.useState(false);
  const [users, setUsers] = React.useState(false);
  const [posts, setPosts] = React.useState(false);

  let history = useHistory();
  const user = React.useContext(UserContext);

  useEffect(() => {
      if (user) {
          history.push('/dashboard')
      }else{
          history.push('/')
      }
  }, [user]);

  useEffect(() => {
      console.log(db)
      fetchUsers();
      fetchPosts();
  },[]);

    const fetchUsers = async()=>{
        const response= db.collection('Users');
        const data=await response.get();
        console.log(data.docs)
        let list = [];
        data.docs.forEach(item=>{
          list.push(item.data())
        })
        setUsers(list);
    }

    const fetchPosts = async()=>{
        const response= db.collection('Posts');
        const data=await response.get();
        console.log(data.docs)
        let list = [];
        data.docs.forEach(item=>{
          list.push(item.data())
        })
        setPosts(list);
        console.log(list)
    }

  const handleClick = () => {
    setChannel(prev => [...prev, `My Channel ${prev.length + 1}`]);
  }

  const onChannelSelect = (val) => {
    setSelectedChannel(val)
  }

  const onSubmit = (e) => {
    if(newPost.subject && newPost.body && e.key === 'Enter'){
      setIsPost(true)
    }
  }

  const onPostChange = (e, val, key) => {
    if(key == 'subject'){
      setNewPost({...newPost, subject: e.target.value})
    }else if(key == 'body'){
      setNewPost({...newPost, body: e.target.value})
    }
  }

  const onPostSelect = (val) => {
    setNewPost({...newPost, body: val.body, subject: val.subject})
    console.log(newPost)
    setIsPost(true)
    }

  return (
    <div className="App">
      <div className="main_dashboard">
        <div className="left_section">
            <button className="logout-button" onClick={logOut}>
                <span> logout</span>
            </button>
            <div className="dashboard">
                <p>@levelshealth.com</p>
                <button onClick={() => handleClick()}>New Channel</button>
                {channel.map(val => <p style={{cursor: 'pointer'}} onClick={() => onChannelSelect(val)}>{val}</p>)}
            </div>            
        </div>
        <div>
            {!isPost && selectedChannel && <Channel channel={selectedChannel} posts={posts} onPostChange={onPostChange} onPostSelect={onPostSelect}  onSubmit={onSubmit}></Channel>}
            {isPost && <Post newPost={newPost} posts={posts} user={user}></Post>}
        </div>
      </div>     
    </div>
  );
}

export default Dashboard;
