import React, { useEffect, useState } from 'react';
import '../src/style/main.css'

function Channel({channel, onPostChange, onSubmit}) {
  useEffect(() => {
    document.addEventListener('keypress', onSubmit);
  })

  return (
    <div className="channel">
      <div>
        <div className='channel-head'>
          <p>@levelshealth.com</p></div>
        <div>
          <p>{channel}</p>
        </div>
        <div>
          <form>
            <label>Subject: </label>
            <input onChange={(e) => onPostChange(e, 'subject')} placeholder='Subject'/>
            <label>Body: </label>
            <textarea onChange={(e) => onPostChange(e, 'body')} placeholder='Post Body'></textarea>
          </form>
        </div>
        <div>
          <p> </p>
        </div>
      </div>
    </div>
  );
}

export default Channel;
