import React, { useState } from 'react';
import '../src/style/main.css'
import { db } from './services/firebase';

function Post({newPost, user, posts}) {
  const [response, setResponse] = React.useState('')
  const [resposeList, setResponseList] = React.useState([])

  const onResponsiveChange = (e) => {
    setResponse(e)
  }

  const handlePostComment = async()=>{
    db.collection("Posts").add({
        email: 'priyanka@gmail.com',
        name: 'priyanka'
    })
        .then((docRef) => {
            alert("Data Successfully Submitted");
        })
        .catch((error) => {
            console.error("Error adding document: ", error);
        });
}

  return (
    <div className="channel">
      <div>
        <div className="heading">
          <p>{newPost.subject}</p>
        </div>
        {<div className="user_name">
            <p>{user.displayName}</p>
            <p>{newPost.body}</p> 
        </div>}
        {resposeList.length ? resposeList.map(val => {
          return <div className="user_name">
            <p>{user.displayName}</p>
            <p>{val}</p> 
        </div>
        }): null}
        <div className="submit">
            <input placeholder='Response' onChange={e => onResponsiveChange(e.target.value)}/>
            <button className="submit_button" onClick={() => setResponseList([...resposeList, response])}>Submit</button>
        </div>
        <div>
          <p> </p>
        </div>
      </div>
    </div>
  );
}

export default Post;
