import React from 'react';
import Channel from './Channel';
import Post from './Post';
import {logOut} from './services/firebase'


function Dashboard() {
  const [channel, setChannel] = React.useState([])
  const [selectedChannel, setSelectedChannel] = React.useState('')
  const [post, setPost] = React.useState({subject: '', body: ''})
  const [isPost, setIsPost] = React.useState(false)

  const handleClick = () => {
    setChannel(prev => [...prev, `My Channel ${prev.length + 1}`]);
  }

  const onChannelSelect = (val) => {
    setSelectedChannel(val)
  }

  const onSubmit = (e) => {
    if(post.subject && post.body && e.key === 'Enter'){
      setIsPost(true)
    }
  }

  const onPostChange = (e, key) => {
    e.preventDefault()
    if(key == 'subject'){
      setPost({...post, subject: e.target.value})
    }else if(key == 'body'){
      setPost({...post, body: e.target.value})
    }
  }

  return (
    <div className="App">
      <div>
        <button className="logout-button" onClick={logOut}>
            <img
                src="https://img.icons8.com/ios-filled/50/000000/google-logo.png"
                alt="google icon"
            />
            <span> logout</span>
        </button>
        <p>@levelshealth.com</p>
        <button onClick={() => handleClick()}>New Channel</button>
        {channel.map(val => <p onClick={() => onChannelSelect(val)}>{val}</p>)}
      </div>
      {!isPost && selectedChannel && <Channel channel={selectedChannel} onPostChange={onPostChange} onSubmit={onSubmit}></Channel>}
      {isPost && <Post post={post}></Post>}
    </div>
  );
}

export default Dashboard;
