import React,{ useEffect } from 'react';
import Channel from './Channel';
import Post from './Post';
import {getDBData, logOut} from './services/firebase'
import { UserContext } from './providers/UserProvider';
import { useHistory } from 'react-router';
import '../src/style/main.css';

function Dashboard() {
  const [channel, setChannel] = React.useState([])
  const [selectedChannel, setSelectedChannel] = React.useState('')
  const [post, setPost] = React.useState({subject: '', body: ''})
  const [isPost, setIsPost] = React.useState(false)

  let history = useHistory();
  const user = React.useContext(UserContext)

  useEffect(() => {
      console.log(user)
      debugger
      if (user) {
          history.push('/dashboard')
      }else{
          history.push('/')
      }
      getDBData.then((snapshot) => {
        if (snapshot.exists()) {
          console.log(snapshot.val());
        } else {
          console.log("No data available");
        }
      }).catch((error) => {
        console.error(error);
      });
  }, [user]);

  const handleClick = () => {
    setChannel(prev => [...prev, `My Channel ${prev.length + 1}`]);
  }

  const onChannelSelect = (val) => {
    setSelectedChannel(val)
  }

  const onSubmit = (e) => {
    if(post.subject && post.body && e.key === 'Enter'){
      setIsPost(true)
    }
  }

  const onPostChange = (e, key) => {
    e.preventDefault()
    if(key == 'subject'){
      setPost({...post, subject: e.target.value})
    }else if(key == 'body'){
      setPost({...post, body: e.target.value})
    }
  }

  return (
    <div className="App">
      <div className="main_dashboard">
        <div className="left_section">
            <button className="logout-button" onClick={logOut}>
                <span> logout</span>
            </button>
            <div className="dashboard">
                <p>@levelshealth.com</p>
                <button onClick={() => handleClick()}>New Channel</button>
                {channel.map(val => <p style={{cursor: 'pointer'}} onClick={() => onChannelSelect(val)}>{val}</p>)}
            </div>            
        </div>
        <div>
            {!isPost && selectedChannel && <Channel channel={selectedChannel} onPostChange={onPostChange} onSubmit={onSubmit}></Channel>}
            {isPost && <Post post={post} user={user}></Post>}
        </div>
      </div>     
    </div>
  );
}

export default Dashboard;
